package VongLap;

public class Loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/**
		 * 1. While
		 */
		int a = 0;
		while(a<10) {
			System.out.println(a);
			a++;
		}
		
		/**
		 * 2. Do while
		 */
		int b = 0;
		do {
			System.out.println(b);
			b++;
		}while(b<10);
		
		/**
		 * 3. For
		 */
		for(int i=0;i<10;i++) {
			System.out.println(i);
		}
	}

}
